import { createStore } from 'vuex'

export default createStore({
  state: {
    posts: []
  },
  getters: {
    getPosts: (state) => {
      return state.posts;
    },
  },

  mutations: {
    setPosts(state, posts) {
      state.posts = posts;
      console.log(posts)
    },
    addPost(state, post) {
      state.posts.push(post);
    },
  },

  actions: {
    async addPostAct({ commit, data }) {
      try {
        console.log("data" + JSON.stringify(data))

        const response = await fetch("http://localhost:3000/post/add-post", {
          method: "POST",
          headers: {"Content-Type": "application/json"},
          credentials: 'include',
          body: JSON.stringify(data)
        });
        const result = await response.json();
        commit('addPost', result); // Assuming your POST request returns the added post
      } catch (e) {
        console.error(e);
      }
    },

    async getAllPostsAct({ commit }) {
      try {
        const response = await fetch(`http://localhost:3000/post/get-all`);
        const data = await response.json();
        commit('setPosts', data);
      } catch (err) {
        console.error(err.message);
      }
    },
  },

  modules: {
  }
})
