import { createStore } from 'vuex'

function formatDate(dateString) {
  const options = { year: 'numeric', month: 'short', day: 'numeric' };
  return new Date(dateString).toLocaleDateString('en-US', options);
}

export default createStore({
  state: {
    posts: []
  },
  getters: {
    getPosts: (state) => {
      return state.posts;
    },
  },

  mutations: {
    setPosts(state, posts) {
      state.posts = posts.map(post => {
        return {
          id: post.id,
          body: post.body,
          date: formatDate(post.date) // Format the date
        };
      });
    },

    addPost(state, post) {
      const formattedPost = {
        body: post.body,
        date: formatDate(post.date)
      };
      state.posts.push(formattedPost);
    }
  },

  actions: {
    async addPostAct({ commit }, data) {
      try {
        const response = await fetch("http://localhost:3000/post/add-post", {
          method: "POST",
          headers: {"Content-Type": "application/json"},
          credentials: 'include',
          body: JSON.stringify(data)
        });
        const result = await response.json();
        commit('addPost', result); // Assuming your POST request returns the added post
      } catch (e) {
        console.error(e);
      }
    },

    async getAllPostsAct({ commit }) {
      try {
        const response = await fetch(`http://localhost:3000/post/get-all`);
        const data = await response.json();
        commit('setPosts', data);
      } catch (err) {
        console.error(err.message);
      }
    },
  },

  modules: {
  }
})
